/** @jsx jsx */

import React from 'react';
import { css, jsx } from '@emotion/core';
import { Link } from 'react-router-dom';

import { 
    Toast, ToastBody, ToastHeader,
    Card, CardImg, CardText, CardBody, CardLink, CardTitle, CardSubtitle, Button
} from 'reactstrap';

const thumbnailStyle = css`
    .Thumbnail {
        background-color: #F3F2EF;
        max-width: 50em;
    }
    .thumbnail-content {
        display: inline-block;
        vertical-align: middle;
        margin: 0;
    }
    .description {
        margin-left: 15px;
        max-width: 30em;
        min-height: 100%;
        overflow-wrap: break-word;
    }
`;

const Thumbnail = (props) => {
  const {
    description,
    header,
    image,
    link,
    technology,
    year,
  } = props;
  return (
    <div css={thumbnailStyle}>
      <div className="Thumbnail">
        { image && 
            <Link className="thumbnail-content" to={link}>
                <img
                    alt={header}
                    src={image || require('assets/empty.png')}
                    style={{
                        width: '15em',
                        objectFit: 'cover',
                        marginBottom: 0,
                        minWidth: 125,
                        maxWidth: '100%',
                        backgroundSize: 'contain',
                        backgroundRepeat: 'no-repeat',
                        backgroundPosition: 'center',
                    }}
                />
            </Link>
        }
        <div className="thumbnail-content description">
          <Link to={link}><h3>{header}</h3></Link>
          <p>{description}</p>
          <p>{technology}</p>
        </div>
      </div>
    </div>
  );
};

export default Thumbnail;
