/** @jsx jsx */

import React from 'react';
import { css, jsx } from '@emotion/core';
import { 
    Toast, ToastBody, ToastHeader,
    Card, CardImg, CardText, CardBody, CardLink, CardTitle, CardSubtitle, Button
} from 'reactstrap';

const thumbnailStyle = css`
    .Navigation {
        display: flex;
        flex-direction: column;
        align-items: right;
        color: #515151;
        position: relative;
        overflow: auto;
        padding: 4em;
    }
`;

const Thumbnail = (props) => {
  const { header, description, technology, year, image, link } = props;
  return (
    <div css={thumbnailStyle}>
      <div className="thumbnail">
        <img
          alt={header}
          src={image || require('assets/empty.png')}
          style={{
            width: '20em',
            height: '15em',
            objectFit: 'cover',
            minWidth: 125,
            background: `url(${require('assets/loading.svg')})`,
            backgroundSize: 'contain',
            backgroundRepeat: 'no-repeat',
            backgroundPosition: 'center',
          }}
        />
      </div>
    </div>
  );
};

export default Thumbnail;
